import React, { useState, useEffect, useRef } from 'react';
import './style.css';

// Configurazione
const API_CONFIG = {
    endpoint: "/api", // Utilizza il proxy di React
    model_name: "mistralai/mistral-7b-instruct:free",
    temperature: 0.7,
    max_tokens: 13107
};

function App() {
    // Elementi DOM
    const chatMessagesRef = useRef(null);
    const userInputRef = useRef(null);
    const sendButtonRef = useRef(null);
    const modelSelectRef = useRef(null);

    // Stato
    const [userInput, setUserInput] = useState('');
    const [messages, setMessages] = useState([]);
    const [isBotTyping, setIsBotTyping] = useState(false);
    const [settings, setSettings] = useState({
        model: API_CONFIG.model_name,
        temperature: API_CONFIG.temperature,
        maxTokens: API_CONFIG.max_tokens
    });
    const [showSettings, setShowSettings] = useState(false);
    const [sidebarOpen, setSidebarOpen] = useState(true);
    const [projects, setProjects] = useState([]);
    const [projectChats, setProjectChats] = useState([]); // Chat del progetto corrente
    const [independentChats, setIndependentChats] = useState([]); // Chat indipendenti
    const [currentProject, setCurrentProject] = useState(null);
    const [currentChat, setCurrentChat] = useState(null);

    // Stato streaming
    const [currentBotMessage, setCurrentBotMessage] = useState('');

    // Inizializzazione
    useEffect(() => {
        loadSupportedModels();
        loadProjects();
        loadIndependentChats();
    }, []);

    // Carica progetti dal backend
    async function loadProjects() {
        try {
            const response = await fetch('/api/projects');
            if (response.ok) {
                const data = await response.json();
                setProjects(data);
                if (data.length > 0) {
                    setCurrentProject(data[0]);
                    loadChats(data[0].id);
                }
            }
        } catch (err) {
            console.error("Errore caricamento progetti:", err);
        }
    }

    // Carica chat per un progetto
    async function loadChats(projectId) {
        try {
            const response = await fetch(`/api/projects/${projectId}/chats`);
            if (response.ok) {
                const data = await response.json();
                setChats(data);
            }
        } catch (err) {
            console.error("Errore caricamento chat:", err);
        }
    }

    // Crea nuovo progetto
    async function createProject() {
        const projectName = prompt("Nome del nuovo progetto:");
        if (!projectName) return;

        try {
            const response = await fetch('/api/projects', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ name: projectName, description: "" })
            });

            if (response.ok) {
                const newProject = await response.json();
                setProjects(prev => [...prev, newProject]);
                setCurrentProject(newProject);
                setChats([]);
            }
        } catch (err) {
            console.error("Errore creazione progetto:", err);
        }
    }

    // Crea nuova chat
    async function createChat() {
        if (!currentProject) {
            alert("Seleziona prima un progetto!");
            return;
        }

        const chatTitle = prompt("Titolo della nuova chat:") || "Nuova Chat";

        try {
            const response = await fetch('/api/chats', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ 
                    project_id: currentProject.id, 
                    title: chatTitle,
                    messages: []
                })
            });

            if (response.ok) {
                const newChat = await response.json();
                setChats(prev => [...prev, newChat]);
                setCurrentChat(newChat);
                setMessages([]);
            }
        } catch (err) {
            console.error("Errore creazione chat:", err);
        }
    }

    // Effetto per lo scroll automatico
    useEffect(() => {
        if (chatMessagesRef.current) {
            chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
        }
    }, [messages, isBotTyping]);

    // Carica modelli dal backend
    async function loadSupportedModels() {
        try {
            const response = await fetch('/models');
            if (response.ok) {
                const data = await response.json();
                console.log("Modelli supportati:", data.models);
            }
        } catch (err) {
            console.error("Errore caricamento modelli:", err);
        }
    }


    // Invio messaggio
    function sendMessage(e) {
        e.preventDefault();
        const text = userInput.trim();
        if (!text) return;

        // Aggiungi messaggio utente
        const userMsg = {
            id: Date.now(),
            role: 'user',
            content: text,
            timestamp: new Date()
        };

        setMessages(prev => [...prev, userMsg]);
        setUserInput('');
        setIsBotTyping(true);
        setCurrentBotMessage('');

        // Ottieni risposta del bot
        getBotResponse(text);
    }

    // Risposta Bot (Streaming)
    async function getBotResponse(userMessage) {
        try {
            const response = await fetch('/v1/chat/completions', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    model: settings.model,
                    messages: [
                        { role: "system", content: "You are Cursor Assistant, a helpful AI coding partner." },
                        { role: "user", content: userMessage }
                    ],
                    temperature: settings.temperature,
                    max_tokens: settings.maxTokens,
                    stream: true
                })
            });

            if (!response.ok) throw new Error(`HTTP ${response.status}`);

            const reader = response.body.getReader();
            const decoder = new TextDecoder();
            let buffer = "";
            let botMessageContent = "";

            while (true) {
                const { done, value } = await reader.read();
                if (done) break;

                buffer += decoder.decode(value, { stream: true });
                const lines = buffer.split("\n");
                buffer = lines.pop();

                for (const line of lines) {
                    if (line.startsWith("data: ")) {
                        const jsonStr = line.slice(6);
                        if (jsonStr === "[DONE]") break;

                        try {
                            const data = JSON.parse(jsonStr);
                            const content = data.choices?.[0]?.delta?.content;
                            if (content) {
                                botMessageContent += content;
                                setCurrentBotMessage(botMessageContent);
                            }
                        } catch (e) {
                            console.warn("Parse error:", e);
                        }
                    }
                }
            }

            // Aggiungi messaggio completo del bot
            const botMsg = {
                id: Date.now(),
                role: 'bot',
                content: botMessageContent,
                timestamp: new Date()
            };

            setMessages(prev => [...prev, botMsg]);
        } catch (error) {
            console.error("Errore:", error);
            const errorMsg = {
                id: Date.now(),
                role: 'bot',
                content: "❌ Errore. Riprova più tardi.",
                timestamp: new Date()
            };
            setMessages(prev => [...prev, errorMsg]);
        } finally {
            setIsBotTyping(false);
            setCurrentBotMessage('');
        }
    }

    // Formatta il contenuto markdown
    function formatMarkdown(text) {
        if (!text) return '';

        return text
            .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
            .replace(/\*(.*?)\*/g, '<em>$1</em>')
            .replace(/`(.*?)`/g, '<code>$1</code>')
            .replace(/^### (.*$)/gm, '<h3>$1</h3>')
            .replace(/^## (.*$)/gm, '<h2>$1</h2>')
            .replace(/^# (.*$)/gm, '<h1>$1</h1>')
            .replace(/^\s*\* (.*$)/gm, '<li>$1</li>')
            .replace(/(<li>.*<\/li>)/gs, '<ul>$1</ul>')
            .replace(/\n/g, '<br>');
    }

    // Determina se il contenuto è codice
    function isCodeContent(content) {
        const patterns = [
            /^\s*[\{\[\(\w]+\s*[=\{\[\(]/,
            /function\s+\w+\s*\(/,
            /(const|let)\s+\w+\s*=/,
            /class\s+\w+/,
            /import\s+.*\s+from/,
            /export\s+(default\s+)?(function|class|const|let)/,
            /\w+\s*\([^)]*\)\s*\{/,
            /```[\s\S]*```/
        ];
        return patterns.some(p => new RegExp(p).test(content));
    }

    // Copia codice negli appunti
    function copyCodeToClipboard(code) {
        navigator.clipboard.writeText(code);
    }

    // Salva impostazioni
    function saveSettings() {
        setSettings({
            model: modelSelectRef.current?.value || settings.model,
            temperature: parseFloat(document.getElementById('temperature')?.value || settings.temperature),
            maxTokens: parseInt(document.getElementById('max-tokens')?.value || settings.maxTokens)
        });
        setShowSettings(false);
    }

    return (
        <div className="app-container">
            {/* Sidebar */}
            <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
                {/* Header Sidebar con pulsante chiudi */}
                <div className="sidebar-header">
                    <h2>Cursor Assistant</h2>
                    <button 
                        className="icon-btn close-sidebar-btn" 
                        onClick={() => setSidebarOpen(false)}
                        title="Chiudi sidebar"
                    >
                        ✕
                    </button>
                </div>

                {/* Sezione Progetti */}
                <section className="sidebar-section">
                    <div className="section-header">
                        <h3>📂 Progetti</h3>
                        <button 
                            className="icon-btn new-project-btn" 
                            onClick={createProject}
                            title="Nuovo Progetto" 
                            aria-label="Crea nuovo progetto"
                        >
                            +
                        </button>
                    </div>
                    <ul className="projects-list">
                        {projects.map(project => (
                            <li 
                                key={project.id} 
                                className={`sidebar-item ${currentProject?.id === project.id ? 'active' : ''}`}
                                onClick={() => {
                                    setCurrentProject(project);
                                    loadChats(project.id);
                                }}
                            >
                                {project.name}
                            </li>
                        ))}
                    </ul>
                </section>

                {/* Sezione Chat Recenti */}
                <section className="sidebar-section">
                    <div className="section-header">
                        <h3>💬 Chat recenti</h3>
                        <button 
                            className="icon-btn new-chat-btn" 
                            onClick={createChat}
                            title="Nuova Chat" 
                            aria-label="Avvia nuova chat"
                        >
                            +
                        </button>
                    </div>
                    <ul className="conversation-history">
                        {chats.map(chat => (
                            <li 
                                key={chat.id} 
                                className={`sidebar-item ${currentChat?.id === chat.id ? 'active' : ''}`}
                                onClick={() => {
                                    setCurrentChat(chat);
                                    // Carica i messaggi della chat qui se necessario
                                }}
                            >
                                {chat.title}
                            </li>
                        ))}
                    </ul>
                </section>

                {/* Footer Sidebar */}
                <footer className="sidebar-footer">
                    <div className="user-info">
                        <div className="user-avatar" aria-hidden="true">👤</div>
                        <span className="user-name">Utente</span>
                    </div>
                    <button className="settings-btn" onClick={() => setShowSettings(true)}>⚙️ Impostazioni</button>
                </footer>
            </aside>

            {/* Main Container */}
            <main className="main-container">
                {/* Pulsante per aprire sidebar quando è chiusa */}
                {!sidebarOpen && (
                    <button 
                        className="open-sidebar-btn"
                        onClick={() => setSidebarOpen(true)}
                        title="Apri sidebar"
                    >
                        ☰
                    </button>
                )}

                {/* Header Chat */}
                <header className="chat-header">
                    <div className="avatar" aria-hidden="true">🤖</div>
                    <h1 id="chat-title">
                        {currentChat ? currentChat.title : 'Cursor Assistant — Nuova Chat'}
                    </h1>
                    <button className="icon-btn delete-chat-btn" title="Elimina Chat" aria-label="Elimina chat corrente">🗑️</button>
                </header>

                {/* Messaggi Chat */}
                <section className="chat-messages" ref={chatMessagesRef} aria-live="polite" aria-atomic="false">
                    {messages.map(msg => (
                        <div key={msg.id} className={`message ${msg.role}`}>
                            <div className="avatar">
                                {msg.role === 'user' ? '👤' : '🤖'}
                            </div>
                            <div className="message-content">
                                {isCodeContent(msg.content) ? (
                                    <pre>
                                        <code>{msg.content.trim()}</code>
                                        <button 
                                            className="copy-button" 
                                            onClick={() => copyCodeToClipboard(msg.content)}
                                        >
                                            Copy
                                        </button>
                                    </pre>
                                ) : (
                                    <div dangerouslySetInnerHTML={{ __html: formatMarkdown(msg.content) }} />
                                )}
                            </div>
                            <div className="message-time">
                                {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </div>
                        </div>
                    ))}

                    {/* Messaggio in streaming */}
                    {isBotTyping && currentBotMessage && (
                        <div className="message bot streaming">
                            <div className="avatar">🤖</div>
                            <div className="message-content">
                                <div dangerouslySetInnerHTML={{ __html: formatMarkdown(currentBotMessage) }} />
                            </div>
                        </div>
                    )}

                    {/* Indicatore di digitazione */}
                    {isBotTyping && !currentBotMessage && (
                        <div className="enhanced-typing-indicator">
                            <div className="avatar">🤖</div>
                            <div className="typing-text">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                        </div>
                    )}
                </section>

                {/* Input Chat */}
                <footer className="chat-input-container">
                    <form className="input-row" onSubmit={sendMessage} autoComplete="off">
                        <label htmlFor="file-input" className="file-upload-btn">
                            📎
                            <input type="file" id="file-input" accept="image/*,.pdf,.txt" hidden />
                        </label>
                        <input
                            type="text"
                            value={userInput}
                            onChange={(e) => setUserInput(e.target.value)}
                            ref={userInputRef}
                            placeholder="Chiedi qualcosa a Cursor Assistant..."
                            aria-label="Inserisci messaggio"
                            required
                        />
                        <button 
                            type="submit" 
                            ref={sendButtonRef} 
                            aria-label="Invia messaggio"
                            disabled={isBotTyping}
                        >
                            ➤
                        </button>
                    </form>
                    <div className="input-footer">
                        <span>Cursor Assistant può commettere errori. Verifica le informazioni importanti.</span>
                    </div>
                </footer>
            </main>

            {/* Modale Impostazioni */}
            {showSettings && (
                <div className="modal" role="dialog" aria-modal="true" aria-labelledby="settings-title">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2 id="settings-title">Impostazioni</h2>
                            <button 
                                className="close-btn" 
                                onClick={() => setShowSettings(false)}
                                aria-label="Chiudi impostazioni"
                            >
                                &times;
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="setting-group">
                                <label htmlFor="model-select">Modello</label>
                                <select 
                                    id="model-select" 
                                    ref={modelSelectRef}
                                    defaultValue={settings.model}
                                >
                                    <option value="mistralai/mistral-7b-instruct:free">Mistral 7B (Free)</option>
                                    <option value="google/gemma-7b-it:free">Gemma 7B (Free)</option>
                                    <option value="meta-llama/llama-3.1-8b-instruct:free">Llama 3.1 8B (Free)</option>
                                    <option value="microsoft/phi-3-mini-128k-instruct:free">Phi-3 Mini (Free)</option>
                                    <option value="openchat/openchat-7b:free">OpenChat 7B (Free)</option>
                                    <option value="neversleep/llama-3-lumimaid-8b:free">Llama 3 Lumimaid (Free)</option>
                                    <option value="sao10k/fimbulvetr-11b-v2:free">Fimbulvetr 11B (Free)</option>
                                </select>
                            </div>

                            <div className="setting-group">
                                <label htmlFor="temperature">
                                    Temperatura: <span id="temperature-value">{settings.temperature}</span>
                                </label>
                                <input 
                                    type="range" 
                                    id="temperature" 
                                    min="0" 
                                    max="1" 
                                    step="0.1" 
                                    defaultValue={settings.temperature}
                                    onChange={(e) => {
                                        document.getElementById('temperature-value').textContent = e.target.value;
                                    }}
                                />
                            </div>

                            <div className="setting-group">
                                <label htmlFor="max-tokens">
                                    Token massimi: <span id="max-tokens-value">{settings.maxTokens}</span>
                                </label>
                                <input 
                                    type="range" 
                                    id="max-tokens" 
                                    min="100" 
                                    max="32768" 
                                    step="100" 
                                    defaultValue={settings.maxTokens}
                                    onChange={(e) => {
                                        document.getElementById('max-tokens-value').textContent = e.target.value;
                                    }}
                                />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button id="save-settings" onClick={saveSettings}>Salva</button>
                            <button id="cancel-settings" onClick={() => setShowSettings(false)}>Annulla</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
